        <div class="row justify-content-center g-lg-4 gy-5" id="news-container">
        <!-- Dynamic News Articles Will Be Injected Here -->
    </div>
</div>

<script>
    // Fetch news dynamically
    fetch('fetch-news.php')
        .then(response => response.json())
        .then(data => {
            const container = document.getElementById('news-container');

            // Check for errors
            if (data.error) {
                container.innerHTML = `<p>Error: ${data.error}</p>`;
                return;
            }

            // Build the news articles
            data.forEach((article, index) => {
                const animationClass = index === 0 ? 'fadeInLeft' : index === 1 ? 'fadeInUp' : 'fadeInRight';
                container.innerHTML += `
                    <div class="col-lg-4 col-md-6 wow animate ${animationClass}" data-wow-delay="300ms" data-wow-duration="1500ms">
                        <div class="single-blog magnetic-item">
                            <div class="blog-img">
                                <img class="img-fluid" src="${article.image || 'assets/img/placeholder.jpg'}" alt>
                                <div class="blog-tag">
                                    <a href="#">${article.category}</a>
                                </div>
                            </div>
                            <div class="blog-content">
                                <ul class="blog-meta">
                                    <li><a href="#">${article.date}</a></li>
                                    <li><a href="#">Comment (${article.comments})</a></li>
                                </ul>
                                <h4><a href="${article.url}" target="_blank">${article.title}</a></h4>
                                <div class="blog-footer">
                                    <div class="read-btn">
                                        <a href="${article.url}" target="_blank">Read More
                                            <svg width="12" height="12" viewBox="0 0 13 13" fill="none" xmlns="http://www.w3.org/2000/svg">
                                                <path d="M0 1H12M12 1V13M12 1L0.5 12"></path>
                                            </svg>
                                        </a>
                                    </div>
                                    <div class="social-area">
                                        <ul>
                                            <li><a href="https://www.facebook.com/"><i class="bx bxl-facebook"></i></a></li>
                                            <li><a href="https://twitter.com/"><i class="bx bxl-twitter"></i></a></li>
                                            <li><a href="https://www.pinterest.com/"><i class="bx bxl-pinterest-alt"></i></a></li>
                                            <li><a href="https://www.instagram.com/"><i class="bx bxl-instagram"></i></a></li>
                                        </ul>
                                        <span><img src="assets/img/home-3/plain-icon.svg" alt></span>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>`;
            });
        })
        .catch(err => {
            console.error('Error fetching news:', err);
            document.getElementById('news-container').innerHTML = `<p>Error loading news articles. Please try again later.</p>`;
        });
</script>

    </div>
